import numpy as np


def _num_transfer(seq, seqtype):
    """Convert DNA/RNA sequence to numerical representation.

    Args:
        seq (str): DNA or RNA sequence
        seqtype (str): "DNA" or "RNA"

    Returns:
        str: Numerical sequence where A=0, C=1, G=2, T/U=3
    """
    if seqtype == "DNA":
        seq = seq.replace("A", "0").replace("C", "1").replace("G", "2").replace("T", "3")
    elif seqtype == "RNA":
        seq = seq.replace("A", "0").replace("C", "1").replace("G", "2").replace("U", "3")

    # Keep only digits (remove any invalid characters)
    seq = ''.join(c for c in seq if c.isdigit())

    return seq


def _num_transfer_loc(num_seq, K):
    """Convert numerical sequence to k-mer location indices.

    Args:
        num_seq (str): Numerical sequence
        K (int): K-mer length

    Returns:
        list: List of k-mer indices in base-4 representation
    """
    loc = []
    for i in range(len(num_seq) - K + 1):
        # Convert k-mer from base-4 to decimal
        kmer = num_seq[i:i + K]
        if len(kmer) == K:
            loc.append(int(kmer, 4))

    return loc


def _loc_transfer_matrix(loc_list, dis_list, K, length):
    """Create transition matrix for k-mers with given distances.

    Args:
        loc_list (list): List of k-mer indices
        dis_list (list): List of allowed distances
        K (int): K-mer length
        length (int): Original sequence length

    Returns:
        np.ndarray: Flattened transition matrix
    """
    matrix = np.zeros((4 ** K, 4 ** K), dtype=np.float64)
    num = 0

    for dis in dis_list:
        for i in range(len(loc_list) - K - dis):
            if i + K + dis < len(loc_list):
                matrix[loc_list[i]][loc_list[i + K + dis]] += 1
        num += (length - 2 * K - dis + 1.0)

    if num > 0:
        matrix = matrix / num

    new_matrix = matrix.flatten()

    return new_matrix


def _matrix_encoding(seq, K, d, seqtype):
    """Encode sequence using gapped k-mer patterns.

    Args:
        seq (str): DNA or RNA sequence
        K (int): K-mer length
        d (int): Maximum gap distance
        seqtype (str): "DNA" or "RNA"

    Returns:
        np.ndarray: Encoded feature vector
    """
    seq = seq.upper()
    length = len(seq)
    num_seq = _num_transfer(seq, seqtype)
    loc = _num_transfer_loc(num_seq, K)

    # Define distance ranges for different d values
    dis = [
        list(range(0, 1)),  # d=0: adjacent
        list(range(1, 2)),  # d=1: gap of 1
        list(range(2, 3)),  # d=2: gap of 2
        list(range(3, 5)),  # d=3-4: gaps of 3-4
        list(range(5, 9)),  # d=5-8: gaps of 5-8
        list(range(9, 17)),  # d=9-16: gaps of 9-16
        list(range(17, 33)),  # d=17-32: gaps of 17-32
        list(range(33, 65))  # d=33-64: gaps of 33-64
    ]

    if d == 1:
        feature = _loc_transfer_matrix(loc, list(range(0, 1)), K, length)
    elif d == 2:
        feature = np.hstack((
            _loc_transfer_matrix(loc, list(range(0, 1)), K, length),
            _loc_transfer_matrix(loc, list(range(1, 2)), K, length)
        ))
    else:
        feature = np.hstack((
            _loc_transfer_matrix(loc, list(range(0, 1)), K, length),
            _loc_transfer_matrix(loc, list(range(1, 2)), K, length)
        ))
        for i in range(2, min(d, len(dis))):
            feature = np.hstack((feature, _loc_transfer_matrix(loc, dis[i], K, length)))

    return feature * 100


def matrix_encoding(seq, K, d, seqtype):
    return _matrix_encoding(seq, K, d, seqtype)


# Example usage and testing
if __name__ == "__main__":
    # Test with a sample DNA sequence
    test_seq = ""
    K = 3
    d = 2
    seqtype = "DNA"

    print(f"Input sequence: {test_seq}")
    print(f"K-mer length: {K}")
    print(f"Max gap distance: {d}")
    print(f"Sequence type: {seqtype}")

    features = matrix_encoding(test_seq, K, d, seqtype)
    print(f"Output feature vector shape: {features.shape}")
    print(f"Feature vector (first 10 elements): {features[:10]}")